mapper.update:
INSERT INTO PRECAUTIONS
( 
	  r_id 
	, noti_title01
	, noti_title02
	, noti_title03
	, noti_title04
	, noti_title05
 )

	SELECT CONCAT('r', (SELECT LPAD(MAX(no)+1,3,'0') FROM PRECAUTIONS ))
  , #{noti_title01}
  , #{noti_title02}
  , #{noti_title03}
  , #{noti_title04} 
  , #{noti_title05}

			UPDATE PRECAUTIONS
			SET 
				 NOTI_TITLE01=#{noti_title01}
				,NOTI_TITLE02=#{noti_title02}
				,NOTI_TITLE03=#{noti_title03}
				,NOTI_TITLE04=#{noti_title04}
				,NOTI_TITLE05=#{noti_title05}		
			WHERE R_ID=#{r_id}
